import React, { useState, useEffect } from 'react'
import '../styles/BandeauBottom.css';
import BoutonAction from "./panels/BoutonAction"
import ForwardIcon from '@material-ui/icons/Forward';
import IconButton from '@material-ui/core/IconButton';

const BandeauBottom = ({
    nbInterFeu,
    nbInterFumee,
    nbInterFeuFum,
    nbInterMarqueurs,
    nbInterOuvrirPorte,
    nbInterFermerPorte,
    nbInterHabitant
}) => {

    const renderFleches = () => {
        return (
            <div className="fleches-container">
                <IconButton 
                    style={{
                        left:"25%",
                        position:"absolute",
                        // right:0
                    }}
                >
                    <ForwardIcon />
                </IconButton>
                <IconButton 
                     style={{
                        // right:0,
                        // width:"50%",
                        // height:"50%"
                    }}
                >
                    <ForwardIcon />
                </IconButton>
                <IconButton 
                     style={{
                        // position:"absolute",
                        // bottom:0,
                        // width:"50%",
                        // height:"50%",
                        // left:"10%"
                    }}
                >
                    <ForwardIcon />
                </IconButton>

                <IconButton 
                    //  style={{
                    //     // position:"absolute",
                    //     right:0
                    // }}
                >
                    <ForwardIcon />
                </IconButton>

            </div>


        )
    }


    return (
        <div className="bandeau-bottom">
            <div className="boutons-container">
                {renderFleches()}
                <BoutonAction
                    action="Eteindre un feu"
                    nb={nbInterFeu}
                />
                <BoutonAction
                    action="Atténuer le feu en fumée"
                    nb={nbInterFeuFum}
                />
                <BoutonAction
                    action="Stopper la fumée"
                    nb={nbInterFumee}
                />
                <BoutonAction
                    action="Détruire porte/mur"
                    nb={nbInterMarqueurs}
                />
                <BoutonAction
                    action="Ouvrir la porte"
                    nb={nbInterOuvrirPorte}
                />
                <BoutonAction
                    action="Fermer la porte"
                    nb={nbInterFermerPorte}
                />
                <BoutonAction
                    action="Transporter un habitant"
                    nb={nbInterHabitant}
                />
            </div>

        </div>


    );
}

export default BandeauBottom;
