import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';

const useStyles = makeStyles((theme) => ({
    list: {
      width: '100%',
      borderWidth:50,
      borderRadius:1,
      borderColor:"black",
      // borderColor:theme.palette.primary.main,
      backgroundColor: theme.palette.background.paper,
      position: 'relative',
      overflow: 'auto',
      maxHeight: 300,
    },
    item:{
      maxHeight:20
    }
   
  }));
  
const ListCompanies=({
    companies,
    input,
    handlerClic
})=>{

    const classes = useStyles();


    return (
        <List className={classes.list} >
            {companies.map((comp,index)=>{

              const indexStr = comp.name.toUpperCase().indexOf(input.toUpperCase())

              return(
                <ListItem button key={index} className={classes.item} onClick={(e)=>{
                  e.preventDefault()
                  handlerClic(comp)
                }}>

                  <ListItemText >  
                    <Typography display="inline" >
                      {comp.name.substring(0, indexStr)}
                    </Typography >

                    <Typography  display="inline">
                      <strong>{comp.name.substring(indexStr, indexStr+input.length)}</strong>
                    </Typography>

                    <Typography display="inline">
                      {comp.name.substring(indexStr+input.length, comp.name.length)}
                    </Typography >
                  </ListItemText>
                </ListItem>
              )
               
            }
            
            
            )}
           
            
        </List>

    )
}

export default ListCompanies
