import React, { useState, useEffect } from 'react'
import '../../styles/InterBandeauBottom.css';
import CaseDonnee from './panels/CaseDonnee'
import Button from '@material-ui/core/Button'
import ContentsAPI from "../../API/ContentsAPI"


const InterBandeauBottom = ({
    numeroPompier,
    vue,
    resultats,
    budget,
    validated,
    handleClicValidated,
    tour,
    currentCycle,
    couts
}) => {
  
    const [textes, setTextes] = useState(null)

    useEffect(() => {
        // console.log(textes)
        // const getTextes = async () => {
        //     const textes = ContentsAPI.getTexts("jeuInterBandeauBottom").then(
        //         (res) => { setTextes(res) }
        //     )
        // }
        // if (textes == null) {
        //     getTextes()
        // }
    }, [textes])

    const getText = (name) => {
        if (textes == null) {
            return name
        } else {
            return textes["label" + name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }

    


    const renderCoutsInterventions = (interventions) => {
        var components = []
        if(interventions){
            Object.keys(couts.interventions).forEach((key,index) => {
                components.push(
                    <span key={index} className="label-cout-inter">
                        {key + " : "}<span className="label-cout-inter-eur">{couts.interventions[key] + " €"}</span>
                    </span>
                )
            });
        }else{
            Object.keys(couts.equipements).forEach((key,index) => {
                components.push(
                    <span key={index} className="label-cout-inter">
                        {key + " : "}<span className="label-cout-inter-eur">{couts.equipements[key] + " €"}</span>
                    </span>
                )
            });
        }
        
        return (
            <div className="couts-container">
                {components}
            </div>
        )
    }

    const renderBudgets = () => {
        return (
            <div className="budgets-container">
                <div
                    className="case-panel donnees-container "
                    style={{
                        flex: 1
                    }}
                >
                    <label
                        className="label-title"
                    >
                        {"Séquence en cours"}
                    </label>
                    <CaseDonnee
                        key={1}
                        name={getText("budgetSeqTot")}
                        value={budget.sequence.total+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={2}
                        name={getText("budgetSeqDep")}
                        value={budget.sequence.depense+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={3}
                        name={getText("budgetSeqDispo")}
                        value={budget.sequence.total-budget.sequence.depense+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                </div>
                <div
                    className="case-panel donnees-container "
                    style={{
                        flex: 1
                    }}
                >
                    <label
                        className="label-title"
                    >
                        {"Cycle en cours"}
                    </label>
                    <CaseDonnee
                        key={1}
                        name={getText("budgetCycleTot")}
                        value={budget.cycle.total+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={2}
                        name={getText("budgetCycleDep")}
                        value={budget.cycle.depense+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                    <CaseDonnee
                        key={3}
                        name={getText("budgetCycleDispo")}
                        value={budget.cycle.total-budget.cycle.depense+" €"}
                        primary={false}
                        flexFactor={0}
                    />
                    
                </div>
            </div>

        )
    }


    const renderBoutonsValider = () => {
        return (
            <div className="boutons-valider-container">
                <Button
                    disabled={numeroPompier !== tour || validated}
                    key={0}
                    className="bouton-valider vert"
                    variant='contained'
                    disableElevation
                    onClick={() => handleClicValidated(vue)}

                >
                    <img src={"assets/validate.png"} alt={"image"} />

                </Button>

                <Button
                    disabled={numeroPompier !== tour || validated}
                    key={1}
                    className="bouton-valider rouge"
                    variant='contained'
                    disableElevation
                // onClick={() => handleClicValidated(false)}
                >
                    <img src={"assets/cancel.png"} alt={"image"} />
                </Button>

            </div>
        )

    }


    const renderResults = () => {
        return (
            <div
                className="case-panel donnees-container"
                style={{
                    flex: 2
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("resultats") + " " + numeroPompier}
                </label>
                <CaseDonnee
                    key={1}
                    name={getText("nbDecedes")}
                    value={resultats.Decedes}
                    primary={false}
                    flexFactor={0}
                />
                <CaseDonnee
                    key={2}
                    name={getText("nbSauves")}
                    value={resultats.Sauves}
                    primary={false}
                    flexFactor={0}
                />
                <CaseDonnee
                    key={3}
                    name={getText("nbMarqueurs")}
                    value={resultats.Marqueur}
                    primary={false}
                    flexFactor={0}
                />
            </div>
        )
    }
    return (
        <>
           { vue=="operations"? 
                <div className="bandeau-bottom left">
                    {renderResults()}
                </div>
            : null}
             { vue=="interventions"? 
                <div className="bandeau-bottom">
                {renderCoutsInterventions(true)}
                {renderBudgets()}
                {renderBoutonsValider()}
            </div>
            : null}
             { vue=="equipements"? 
                <div className="bandeau-bottom">
                {renderCoutsInterventions(false)}
                {renderBudgets()}
                {renderBoutonsValider()}
            </div>
            : null}
            
        </>
    );
}

export default InterBandeauBottom;
