import React,{useState,useEffect} from 'react'

const CaseGrille = ({
    murs,
    type
}) => {
    
    const colorMur="black"
    const colorVide="black"
    const largeurBorderMur=5
    const largeurBorderCase=0.5

    const colors=[
        "white", // VIDE
        "red", //Pompier
        "green", // Habitant
        "yellow", // Flamme
        "gray" // Fumee
    ]
    return ( 
       <div 
       className="case-grille" 
       style={{
            borderTopColor:murs[0] ? colorMur : colorVide,
            borderRightColor:murs[1] ? colorMur : colorVide,
            borderBottomColor:murs[2] ? colorMur : colorVide,
            borderLeftColor:murs[3] ? colorMur : colorVide,
            borderTopWidth:murs[0] ? largeurBorderMur: largeurBorderCase,
            borderRightWidth:murs[1] ? largeurBorderMur : largeurBorderCase,
            borderBottomWidth:murs[2] ? largeurBorderMur : largeurBorderCase,
            borderLeftWidth:murs[3] ? largeurBorderMur :largeurBorderCase,
            
       }}>

       </div>

    );
}
 
export default CaseGrille;
