import React,{useState,useEffect} from "react";

// import { Button, FormGroup, FormControl, ControlLabel } from "react-bootstrap";
import { TextField } from '@material-ui/core'
import API from "../API/API";

const SearchField = ()=> {
    const getData = async(str)=> {
        const data =  await API.companies(str)
        if (data!=companies){
            setCompanies(data)
        }
       
    }

    const [companies,setCompanies]=useState([])
    const [components,setComponents]=useState([])
   
    useEffect(()=>{
        var components =[]
        const str = document.getElementById("standard-basic").value
    
        //if(companies.length==0){
           // getData("")
            companies.map((comp,index) => {
                components.push(
                <p key={index} id="standard-basic">
                    {comp.name.indexOf(str)}
                </p>
            )
            });
            setComponents(components)
       // }
        

    },[companies])
 
    const onTextChange = (e)=>{
        getData(e.target.value)
    }


    return(
        <div className="Login">
        <TextField id="standard-basic" label="Company name" onChange={e=>onTextChange(e)} />
            {components}
        
        
        </div>
    )
}

export default SearchField
