import React,{useState,useEffect} from 'react'
import Button from '@material-ui/core/Button'
import '../../styles/BandeauTop.css';
// import ImageExtFeu from "../../assets/ExtincteurFeu.png"

const BoutonAction = ({
    action,
    nb,
    image
}) => {
    

    return (  
        <Button className="bouton-action" variant='contained' disableElevation>
            {/* <div> */}

            

                <label class="title-action">{action+"\n"}</label>
                <img src={require('../../assets/ExtincteurFeu.png')} alt={"image"}/>
                {nb>1 ? 
                <label class="count-action">{nb+" interventions"}</label>

                :

                <label class="count-action">{nb+" intervention"}</label>
                }
            {/* </div> */}
            
        </Button>
            
       

    );
}
 
export default BoutonAction;
