import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CaseGrille from "../CaseGrille"
import MurGrille from "../MurGrille"

import Grid from '@material-ui/core/Grid';
import '../Grille.css';




const Piece = ({
    carte,
    position,
    nbLignes,
    nbColonnes,
    maxColonnesPiece,
    maxLignesPiece

}) => {


    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignes;i++){
             for(let j=0;j<nbColonnes;j++){
                grille.push( 
                    <Grid item xl={6} md={6} sm={12}
                        key={i+","+j} 
                        item
                        xs={4}
                    >
                        <CaseGrille 
                            murs={[0,1,2,3]}
                            type={carte[i+position[1]][j+position[0]]}
                        />
                         {/* <MurGrille 
                            type={0}
                            vertical={true}
                        /> */}
                    </Grid> 
                )
             }
            
        }
        return grille
    }



    return (  

    <Grid  container className="piece"  spacing={4} >
       {renderGrille()}
    </Grid>
        
        
    );
}
 
export default Piece;
