import React,{useState,useEffect} from 'react'
import '../styles/BandeauBottom.css';
import BoutonAction from "./panels/BoutonAction"
import ForwardIcon from '@material-ui/icons/Forward';
import Button from '@material-ui/core/Button'

const BandeauBottom = ({
    nbInterFeu,
    nbInterFumee,
    nbInterFeuFum,
    nbInterMarqueurs,
    nbInterOuvrirPorte,
    nbInterFermerPorte,
    nbInterHabitant
}) => {
    
    const renderFleches=()=>{
        return(

            <Button variant="contained" disableElevation><ForwardIcon/></Button>        

        )
    }


    return (  
        <div className="bandeau-bottom">
            <div className="boutons-container">
                <BoutonAction 
                    action="Eteindre un feu"
                    nb={nbInterFeu}
                />
                <BoutonAction 
                    action="Atténuer le feu en fumée"
                    nb={nbInterFeuFum}
                />
                <BoutonAction 
                    action="Stopper la fumée"
                    nb={nbInterFumee}
                />
                <BoutonAction 
                    action="Détruire porte/mur"
                    nb={nbInterMarqueurs}
                />
                <BoutonAction 
                    action="Ouvrir la porte"
                    nb={nbInterOuvrirPorte}
                />
                <BoutonAction 
                    action="Fermer la porte"
                    nb={nbInterFermerPorte}
                />
                <BoutonAction 
                    action="Transporter un habitant"
                    nb={nbInterHabitant}
                />
            </div>
           
        </div>
       

    );
}
 
export default BandeauBottom;
