import React, { useEffect, useState, useRef } from 'react';
import Grid from "@material-ui/core/Grid"
import Paper from "@material-ui/core/Paper"
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import TableFooter from '@material-ui/core/TableFooter';
import TablePagination from '@material-ui/core/TablePagination';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import TextField from '@material-ui/core/TextField';

import '../../styles/Interfaces.css';


const InterInterventions = ({
    budget,
    cycles,
    handleBudgetChange,
    type,
    currentCycle,
    budgetSim,
    couts

}) => {
    const lastRow = useRef(null)
    const nbCycleParSeq = 2
    const nbPompiers = 6

    const [numCycle, setNumCycle] = useState(1)
    const [lastCycle, setLastCycle] = useState(budgetSim)

    const [page,setPage]=useState(0)

    const renderHeaders = () => {
        var components = []
        components.push(
            <TableCell key={0} align="center" component="th">Séquence</TableCell>
        )
        components.push(
            <TableCell key={1} align="center" component="th">Cycle</TableCell>
        )
        if(type=="equipements"){
            components.push(
                <TableCell key={2} align="center" component="th">Type</TableCell>
            )
        }
        
        for (let i = 1; i <= nbPompiers; i++) {
            components.push(
                <TableCell key={i+" : "+type=="equipements" ? 2 : 1} align="center" component="th">Pompier {i}</TableCell>
            )
        }
        components.push(
            <TableCell key={1000} align="center" component="th">Total</TableCell>
        )
        return ( 
            <TableRow key={"header"}>
                {components}
            </TableRow>
        )
    }


    const styles = {
        root: {
            background: "black"
        },
        input: {
            color: "white"
        }
    };


    const renderPropositionsBudget = (nCycle, numPompier) => {
        
        let cycle = cycles.sort((a, b) => a.numCycle - b.numCycle)[nCycle - 1]
        if(cycle==null && nCycle>cycles.length) cycle=lastCycle
        let sum = 0
        if(cycle!=null){
            Object.keys(cycle).forEach((key, index) => {
                if (key != "numCycle" && key!=="numSeq" && (numPompier !== index-1 || nCycle<numCycle) ) {
                    sum += cycle[key]
                }
            })
        }
       
        var components = []
        const maxValue =cycle!=null && nCycle<numCycle ?  Math.floor((sum ) / 100) : Math.floor((budget.sequence.total - budget.sequence.depense - sum) / 100)
        

        for (let i = 0; i <=maxValue; i++) {
            components.push(
                <MenuItem key={i} value={i * 100} align="right" >{i * 100 + "€"}</MenuItem>
            )
        }
        return components
    }
    const renderSelectBudgetCycle = (nCycle, disabled, numPompier, value) => {
        return (
            <FormControl disabled={disabled} variant="outlined" key={numPompier}>
                <Select
                    value={value}
                    onChange={(value) => handleChangeSelect(numPompier, value)}
                    displayEmpty
                    color="secondary"
                >
                    <MenuItem value="" disabled align="center">
                        Budget {numPompier}
                    </MenuItem>

                    {renderPropositionsBudget(nCycle, numPompier)}

                </Select >
            </FormControl>
        )
    }
    const renderRowBudgetCycle = (cycle) => {
        if(cycle.numCycle<currentCycle+50){
            var comp = []
            var sum = 0
            if(cycle.numCycle%2==1){
                comp.push(
                    <TableCell key={0} scope="row" align="center" component="th" rowSpan={nbCycleParSeq}>
                        Séquence n°{cycle.numSeq}
                    </TableCell>
                )
            }
            
            comp.push(
                <TableCell key={1} scope="row" align="center" component="th" >
                    Cycle n°{cycle.numCycle}
                </TableCell>
            )
    
            Object.keys(cycle).forEach((key, index) => {
                if (key != "numCycle" && key!="numSeq") {
                    comp.push(
                        <TableCell key={index} align="center">{renderSelectBudgetCycle(cycle["numCycle"], numCycle != cycle["numCycle"], index-1, cycle[key])}</TableCell>
                    )
                    sum += cycle[key]
                }
            })
            comp.push(
                <TableCell scope="row" align="center" key={1000} >
                    <TextField
                        id="outlined-basic"
                        variant="outlined"
                        disabled
                        value={sum + " €"}
                        inputProps={{
                            style: {
                                color: numCycle == cycle["numCycle"] ? sum > budget.sequence.total - budget.sequence.depense ? "red" : "green" : null,
                                fontWeight: numCycle == cycle["numCycle"] ? "bold" : null,
                                fontStyle: numCycle != cycle["numCycle"] ? "italic" : null,
                                textAlign: "center",
                                backgroundColor: "#f1f1f1",
                                borderRadius: 5,
                                minWidth: 70
    
                            },
                        }}
                    />
    
    
                </TableCell>
            )
            return (
                <TableRow hover key={cycle.numSeq+" : "+cycle.numCycle} ref={cycle.numCycle>=currentCycle ? lastRow : null}>
                    {comp}
                </TableRow>
            )

        }else{
            return null
        }
    }
    const renderBudgetsCycles = () => {
        if(cycles.length+1!==lastCycle.numCycle) initLastCycle()
        const cyclesSorted = cycles.sort((a, b) => a.numCycle - b.numCycle)
        var components = []
        cyclesSorted.forEach((cycle,index) => {
            if(cycle.numSeq==page+1){
                components.push(
                    renderRowBudgetCycle(cycle)
                )
            }

        })
        if(page==lastCycle.numSeq-1   ){
            components.push(renderRowBudgetCycle(lastCycle))
        }
        
        return (
            components
        )
    }

    const handleChangeSelect = (numPompier, event,key) => {
        var c = {...lastCycle}
        if(type=="interventions"){
            c["pomp" + numPompier] = event.target.value
        }else{
            c[key]["pomp" + numPompier] = event.target.value
        }
        setLastCycle(c)
        handleBudgetChange(type,c)
    }

    const initLastCycle=()=>{
        setLastCycle(
            type=="interventions" ? 
            {
                numSeq:1,
                numCycle: cycles.length+1,
                pomp1: 0,
                pomp2: 0,
                pomp3: 0,
                pomp4: 0,
                pomp5: 0,
                pomp6: 0,
               
            }
            :
            {
                numSeq:1,
                numCycle: cycles.length+1,
                Feu:{
                    pomp1: 0,
                    pomp2: 0,
                    pomp3: 0,
                    pomp4: 0,
                    pomp5: 0,
                    pomp6: 0,
                },
                FeuFum:{
                    pomp1: 0,
                    pomp2: 0,
                    pomp3: 0,
                    pomp4: 0,
                    pomp5: 0,
                    pomp6: 0,
                },
                Fum:{
                    pomp1: 0,
                    pomp2: 0,
                    pomp3: 0,
                    pomp4: 0,
                    pomp5: 0,
                    pomp6: 0,
                },
                Marqueur:{
                    pomp1: 0,
                    pomp2: 0,
                    pomp3: 0,
                    pomp4: 0,
                    pomp5: 0,
                    pomp6: 0,
                }
            }
        )
    }
    

    useEffect(() => {
        console.log(lastCycle)
        setNumCycle(cycles.length+1)
        // lastRow.current.scrollIntoView()
    }, [lastCycle])

    const renderPropositionsStock= (nCycle, numPompier,key) => {
        let cycle = cycles.sort((a, b) => a.numCycle - b.numCycle)[nCycle - 1]
        if(cycle==null && nCycle>cycles.length) cycle=lastCycle
        let sum = 0
        if(cycle!=null){
            Object.keys(cycle).forEach((k) => {
                
                if (k != "numCycle" && k!=="numSeq" && k!=="type"  ) {
                    Object.values(cycle[k]).forEach((value,index) => {
                        if((numPompier !== index+1 || nCycle<numCycle)){
                            sum += value*couts.equipements["coutEq"+k]
                        }
                      });
                }
            })
        }       
        var components = []
        const maxValue =cycle!=null && nCycle<numCycle ?  Math.floor((sum ) / couts.equipements["coutEq"+key]) : Math.floor((budget.sequence.total - budget.sequence.depense - sum )/ couts.equipements["coutEq"+key])
        // const maxValue = Math.floor((sum ) / couts.equipements["coutEq"+key]) 
        // if(maxValue>0) console.log(key,numPompier,maxValue)
        for (let i = 0; i <=maxValue; i++) {
            components.push(
                <MenuItem key={i} value={i } align="right" >{i}</MenuItem>
            )
        }
        return components
    }

    const renderSelectStockCycle = (nCycle, disabled, numPompier, value,key) => {
        return (
            <FormControl disabled={disabled} variant="outlined" key={numPompier}>
                <Select
                    value={value}
                    onChange={(value) => handleChangeSelect(numPompier, value,key)}
                    // displayEmpty
                    color="secondary"
                >
                    <MenuItem value="" disabled align="center">
                        Stock {numPompier}
                    </MenuItem>

                    {renderPropositionsStock(nCycle, numPompier,key)}

                </Select >
            </FormControl>
        )
    }

    const renderRowStocksCycle = (cycle,key,data) => {
        if(cycle.numCycle<currentCycle+2){
            var comp = []
            var sum = 0

            // console.log(key,data)
            const nbLignesParCycle =  (Object.keys(lastCycle).length-2)
            
            if(key=="Feu"){
                // if(cycle.numCycle%nbCycleParSeq==1){
                    comp.push(
                        <TableCell key={1} scope="row" align="center" component="th" rowSpan={nbCycleParSeq*(Object.keys(lastCycle).length-2)}>
                            Séquence n°{cycle.numSeq}
                        </TableCell>
                    )
                // }
                comp.push(
                    <TableCell key={1} scope="row" align="center" component="th" rowSpan={Object.keys(lastCycle).length-2}>
                        Cycle n°{cycle.numCycle}
                    </TableCell>
                )
            }
          
            comp.push(
                <TableCell key={2} scope="row" align="center" component="th" >
                   {key}
                </TableCell>
            )
            if(data!==null && data!==undefined ){
                Object.keys(data).forEach((k, index) => {
                    if (k != "numCycle" && k!="numSeq" && k!=="type") {
                        comp.push(
                            <TableCell key={index+3} align="center">{renderSelectStockCycle(cycle["numCycle"], numCycle != cycle["numCycle"], index+1, data[k],key)}</TableCell>
                        )
                        sum += data[k]*couts.equipements["coutEq"+key]
                    }
                })
            }
           
            comp.push(
                <TableCell scope="row" align="center" key={1000} >
                    <TextField
                        id="outlined-basic"
                        variant="outlined"
                        disabled
                        value={sum + " €"}
                        inputProps={{
                            style: {
                                color: numCycle == cycle["numCycle"] ? sum > budget.sequence.total - budget.sequence.depense ? "red" : "green" : null,
                                fontWeight: numCycle == cycle["numCycle"] ? "bold" : null,
                                fontStyle: numCycle != cycle["numCycle"] ? "italic" : null,
                                textAlign: "center",
                                backgroundColor: "#f1f1f1",
                                borderRadius: 5,
                                minWidth: 70
    
                            },
                        }}
                    />
                </TableCell>
            )

            return (
                <TableRow hover key={cycle.numSeq+" : "+cycle.numCycle+" : "+key} ref={cycle.numCycle>=currentCycle ? lastRow : null}>
                    {comp}
                </TableRow>
            )

        }else{
            return null
        }
    }

    const renderStocksCycle = () => {
        if(cycles.length+1!==lastCycle.numCycle) initLastCycle()
        const cyclesSorted = cycles.sort((a, b) => a.numCycle - b.numCycle)
        var components = []
        cyclesSorted.forEach((cycle,index) => {
            if(index==page){
                Object.keys(cycle).forEach(key => {
                    if(key!=="numSeq" && key!=="numCycle"){
                        components.push(
                            renderRowStocksCycle(cycle,key,cycle[key])
                        )
                    }
                });
            }
        })
        if(page==lastCycle.numCycle-1   ){
            Object.keys(lastCycle).forEach(key => {
                if(key!=="numSeq" && key!=="numCycle"){
                    components.push(renderRowStocksCycle(lastCycle,key,lastCycle[key]))
                    
                }
            });
        }
       
        
        return (
            components
        )
    }

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
        console.log("new Page",newPage)
      };



    return (
        <div className="container">
            <TableContainer component={Paper} className="tableau">
                <Table stickyHeader  size="small" >
                    <TableHead>
                        {renderHeaders()}
                    </TableHead>
                    <TableBody>
                        {
                        type=="interventions" ? 
                            renderBudgetsCycles()

                        : null 
                        }
                        {
                        type=="equipements" ? 
                            renderStocksCycle()

                        : null 
                        }

                    </TableBody>
                    <TableFooter>
                    <TableRow>
                        <TablePagination
                        rowsPerPageOptions={Object.keys(lastCycle).length-2}
                        // colSpan={3}
                        count={cycles.length+1}
                        rowsPerPage={7}
                        page={page}
                        labelDisplayedRows={({ from, to, count }) =>type=="interventions" ?  "Séquence n° "+1 : "Séquence n°"+1+"    |    Cycle n° "+to+" / "+count  }
                        // SelectProps={{
                        //     inputProps: { 'aria-label': 'rows per page' },
                        //     native: true,
                        // }}
                        onChangePage={handleChangePage}
                        // onRowsPerPageChange={handleChangeRowsPerPage}
                        // ActionsComponent={TablePaginationActions}
                        />
                    </TableRow>
                    </TableFooter>
                </Table>
            </TableContainer>
        </div>
    )
}

export default InterInterventions;
