import React,{useState,useEffect} from "react";

import { TextField,FormControlLabel,Checkbox,Grid } from '@material-ui/core'
import API from "../API/API";
import InputAdornment from '@material-ui/core/InputAdornment';
import BusinessCenterRoundedIcon from '@material-ui/icons/BusinessCenterRounded';

const SearchField = ({
    handlerSearch,
    selectedCompany
})=> {

    const [input,setInput]=useState(selectedCompany==="" ? "" : selectedCompany)
    const [clicked,setClicked]=useState(selectedCompany)
    const [els,setEls]=useState(false)

    const [companies,setCompanies]=useState([])

    const getData = async()=> {
        
        const data =  await API.companies(input,els)
        if (data!==companies){
            setCompanies(data)
        }
       
    }

    
    useEffect(()=>{
        if (selectedCompany!==clicked){
            setClicked(selectedCompany)
            setInput(selectedCompany)
        }
    })

    useEffect(()=>{
        getData()
    },[input,els])
    
   
    useEffect(()=>{
        handlerSearch(companies,input)
    },[companies])
 
    const onTextChange = (e)=>{
        e.preventDefault()
        setInput(e.target.value)
    }

    const onCheckChange=(e)=>{
        //e.preventDefault()
        setEls(e.target.checked)
    }

    return(
        <div className="search-field">
        <Grid container spacing={1} alignItems="flex-end">
          <Grid item>
            <AccountCircle />
          </Grid>
          <Grid item>
            <TextField id="input-with-icon-grid" label="With a grid" />
          </Grid>
        </Grid>
        <TextField 
            id="standard-basic" 
            label={"Company name"}
            onChange={e=>onTextChange(e)} 
            variant="outlined"
            value={input===selectedCompany ? selectedCompany : input}
            InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    <BusinessCenterRoundedIcon />
                  </InputAdornment>
                ),
              }}
            //helperText="Please enter a company name"
        />

        <FormControlLabel
            control={
            <Checkbox
                // checked={state.checkedB}
                onChange={e=>onCheckChange(e)}
                name="ELS"
                color="primary"
            />
            }
            label="ELS"
        />
        </div>
    )
}

export default SearchField
