import '../styles/App.css';
import '../styles/Login.css'
import { useEffect, useState } from 'react';
import { TextField, Button, Divider, Typography } from "@material-ui/core"
import UsersAPI from "../API/UsersAPI"
import { useHistory } from 'react-router-dom';
import MyTheme from '../styles/MyTheme';
import { ThemeProvider } from '@material-ui/styles';

import ReactSession from 'react-client-session';
ReactSession.setStoreType("localStorage");

const Login = ({
  handleLogin,
  userConnected

}) => {


  const history = useHistory()

  const [user, setUser] = useState(userConnected)

  const handleConnect = async (e) => {
    e.preventDefault()
    const response = await UsersAPI.login(dataConnection)
    if (response.id != null) {
      setUser(response)
    } else {
      handleLogin(null)
    }
  }

  useEffect(() => {
    if (user != null) {
      ReactSession.set("user",JSON.stringify(user))
      handleLogin(user)
      if (user.role.includes("TACT")) {
        history.push("/tact");
      } else if (user.role.includes("INTER")) {
        history.push("/inter");
      }

    }
  }, [user])

  const [dataConnection, setDataConnection] = useState({
    identifiant: "",
    mdp: ""
  })



  const onChange = (e) => {
    var data = {
      identifiant: dataConnection.identifiant,
      mdp: dataConnection.mdp
    }
    data[e.target.name] = e.target.value
    setDataConnection(data)
  }




  return (
    <ThemeProvider theme={MyTheme}>
      <div
        className="body-login"
      >

        <Typography variant="h1" color="primary">Connexion</Typography>
        <Typography variant="subtitle1">Texte √† ajouter</Typography>



        <div
          className="login-image-container"
        >
          <img
            src='./assets/Camion.png'
          />
          <Divider orientation="vertical" flexItem />
          <form method="post" className="login-form" >
            <TextField
              required
              label="Identifiant"
              variant="outlined"
              name={"identifiant"}
              value={dataConnection.identifiant}
              onChange={onChange}
              className="login-field"
              color="primary"
            />
            <TextField
              required
              label="Mot de passe"
              variant="outlined"
              name="mdp"
              value={dataConnection.mdp}
              onChange={onChange}
              type="password"
              className="login-field"
              color="primary"

            />
            <div className="login-buttons-container">
              <Button
                variant="contained"
                onClick={() => history.push("/")}
                className="login-button retour"
                color="default"
                
              >
                Retour
              </Button>
              <Button
                variant="contained"
                onClick={handleConnect}
                className="login-button connect"
                color="primary"

              >
                Se connecter
              </Button>
            </div>
          </form>

        </div>

      </div>
    </ThemeProvider>
  );
}

export default Login;
