import React, { useState, useEffect } from 'react'
import CaseDonnee from './panels/CaseDonnee'
import "../../styles/BandeauTopInter.css"
import BookmarkRoundedIcon from '@material-ui/icons/BookmarkRounded';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import ToggleButtonGroup from '@material-ui/lab/ToggleButtonGroup';
import ToggleButton from '@material-ui/lab/ToggleButton';

import ContentsAPI from "../../API/ContentsAPI"

const BandeauTopInter = ({
    numeroPompier,
    numEquipe,
    cycle,
    tour,
    resultats,
    configuration,
    chrono,
    handleViewChange,
    vue

}) => {
    const [textes, setTextes] = useState(null)

    // useEffect(() => {
    //     // console.log("use effect")
    //     const getTextes = async () => {
    //         const textes = ContentsAPI.getTexts("jeuBandeauTopInter").then(
    //             (res) => { setTextes(res) }
    //         )
    //     }
    //     if (textes == null) {
    //         getTextes()
    //     }
    // }, [textes])



    const getText = (name) => {
        if (textes == null) {
            return name
        } else {
            return textes["label" + name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }



    // const renderStocks = () => {
    //     return (
    //         <div
    //             className="case-panel donnees-container"
    //             style={{
    //                 flex: 3
    //             }}
    //         >
    //             <label
    //                 className="label-title"
    //             >
    //                 {getText("stockEquipements")}
    //             </label>
    //             <div className="sous-donnees-container">
    //                 <CaseDonnee
    //                     key={0}
    //                     name={"nbExtFeu"}
    //                     value={stocks.Feu}
    //                     primary={false}
    //                     flexFactor={1}
    //                     textes={textes}

    //                 />
    //                 <CaseDonnee
    //                     key={1}
    //                     name={"nbExtFum"}
    //                     value={stocks.Fum}
    //                     primary={false}
    //                     flexFactor={1}
    //                     textes={textes}

    //                 />
    //             </div>
    //             <div className="sous-donnees-container">
    //                 <CaseDonnee
    //                     key={2}
    //                     name={"nbExtFeuFum"}
    //                     value={stocks.FeuFum}
    //                     primary={false}
    //                     flexFactor={2}
    //                     textes={textes}

    //                 />
    //                 <CaseDonnee
    //                     key={3}
    //                     name={"nbMarqueurs"}
    //                     value={stocks.Marqueur}
    //                     primary={false}
    //                     flexFactor={2}
    //                     textes={textes}

    //                 />
    //             </div>
    //         </div>
    //     )
    // }

    // const renderResults = () => {   
    //     return (
    //         <div
    //             className="case-panel donnees-container"
    //             style={{
    //                 flex: 2
    //             }}
    //         >
    //             <label
    //                 className="label-title"
    //             >
    //                 {getText("resultats") + " " + numeroPompier}
    //             </label>
    //             <CaseDonnee
    //                 key={4}
    //                 name={getText("nbDecedes")}
    //                 value={resultats.Decedes}
    //                 primary={false}
    //                 flexFactor={0}
    //             />
    //             <CaseDonnee
    //                 key={5}
    //                 name={getText("nbSauves")}
    //                 value={resultats.Sauves}
    //                 primary={false}
    //                 flexFactor={0}
    //             />
    //         </div>
    //     )
    // }

    const renderNumEquipe = () => {
        return (
            <div
                className="case-panel"
                style={{
                    flex: 1
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("numEquipe")}
                </label>
                <div className="donnees-container">
                    <label
                        className="label-title label-num-pompier"
                    >
                        {numEquipe}
                    </label>
                    <BookmarkRoundedIcon
                        className="icone-num-pompier"
                        fontSize="large"
                        stroke={"white"}
                        strokeWidth={1}
                    />
                </div>
            </div>
        )
    }

    const renderCircleTours = () => {
        var type = 1, //circle type - 1 whole, 0.5 half, 0.25 quarter
            radius = 20, //distance from center
            start = -90, //shift start from 0
            // $elements = $('li:not(:first-child)'),
            numberOfElements = configuration.nbPompEquipe.valeur,
            slice = 360 * type / numberOfElements;

        const renderCircleItems = () => {
            var items = []
            for (let i = 0; i < numberOfElements; i++) {
                const rotate = slice * i + start
                const rotateReverse = rotate * -1
                items.push(
                    <li
                        key={i}
                        style={{
                            'transform': 'rotate(' + rotate + 'deg) translate(' + radius + 'px) rotate(' + rotateReverse + 'deg) translate(' + -radius / 2 + 'px)',
                            "backgroundColor": i + 1 == tour ? "#BF0000" : null
                        }}

                    >
                        {i + 1}
                    </li>

                )
            }
            return items
        }

        return (
            <div
                className="case-panel"
                style={{
                    flex: 1
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("tourParTour")}
                </label>
                <ul >
                    {renderCircleItems()}
                </ul>
            </div>
        )
    }

    const renderFeu = () => {
        return (
            <div
                className="case-panel"
                style={{
                    flex: 1
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("play")}
                </label>

                <div className="donnees-container">
                    <div className='feu'>
                        <div style={{ backgroundColor: numeroPompier != tour ? " #ff0000" : "#9F9F9F" }}></div>
                        <div
                            className={chrono < 30 ? "clignotant" : ""}
                            style={{ backgroundColor: numeroPompier == tour ? chrono < 30 ? "#E26F00" : "#4dff4d" : "#9F9F9F" }}
                        ></div>
                    </div>
                    <span className="case-value" style={{
                        marginTop: "10%",
                        width: "100%",
                        maxWidth: "100%"
                    }} >
                        {(Math.floor(chrono / 60)).toString().padStart(2, "0") + " : "}
                        <span style={{ color: chrono <= 30 ? "red" : "inherit", marginLeft: 5 }}> {" " + (chrono % 60).toString().padStart(2, "0")} </span>
                    </span>
                </div>





            </div>
        )
    }




    return (
        configuration != null ?
            <>
                <div className="bandeau-top">

                    <div className="cases-container">
                        <CaseDonnee
                            name={"cycle"}
                            value={cycle}
                            primary={true}
                            flexFactor={1}
                            textes={textes}
                            data={configuration.nbSequences.valeur * configuration.nbCyclesParSequence.valeur}
                        />
                        <CaseDonnee
                            name={"tour"}
                            value={tour}
                            primary={true}
                            flexFactor={1}
                            textes={textes}

                        />
                        {/* <CaseDonnee
                        name={"budgetRestant"}
                        value={budget+" €"}
                        primary={true}
                        flexFactor={1}
                        textes={textes}

                    /> */}
                        {renderCircleTours()}
                        {renderNumEquipe()}
                        {renderFeu()}

                    </div>

                </div>


                <div className="bandeau-top-right">
                    <ToggleButtonGroup
                        value={vue}
                        exclusive
                        onChange={handleViewChange}
                        aria-label="text alignment"
                    >
                        <ToggleButton value="left" aria-label="left aligned">
                            <label
                                className="label-vue"
                            >Opérations</label>
                        </ToggleButton>
                        <ToggleButton value="center" aria-label="centered">
                            <label
                                className="label-vue"
                            >Interventions</label>
                        </ToggleButton>
                        <ToggleButton value="right" aria-label="right aligned">
                            <label
                                className="label-vue"
                            >Equipements</label>
                        </ToggleButton>
                    </ToggleButtonGroup>
                </div>

            </>


            :
            null


    );
}

export default BandeauTopInter;
