import io from 'socket.io-client'

const urlBack = "http://localhost:4000";
const socket = io.connect(urlBack)

export default {

    nouveauTour:async(currentPomp,carte,mursH,mursV,currentSequence,currentCycle,currentTour,resultatsDecedes,resultatsSauves,session,equipe)=>{

        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                carte:carte,
                mursH:mursH,
                mursV:mursV,
                currentPomp:currentPomp,
                currentSequence:currentSequence,
                currentCycle:currentCycle,
                currentTour:currentTour,
                resultatsDecedes:resultatsDecedes,
                resultatsSauves:resultatsSauves,
                session:session,
                equipe:equipe
            })
        };
        socket.emit(
            'changementTour',
            {
                currentSequence:parseInt(currentSequence),
                currentPomp :parseInt(currentPomp),
                currentTour :parseInt(currentTour),
                currentCycle :parseInt(currentCycle),
            }
        )

        const response = await  fetch(urlBack+"/nouveauTour", requestOptions)


        return await response.json()

    },
    getSocket:()=>{
        return socket
    },
    getConfiguration:async(session)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({session:session})
        };
        const response = await  fetch(urlBack+"/configuration", requestOptions)
        return  await response.json()
    },
    getPlayState:async(session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                session:session,
                equipe:equipe
            })
        };
        const response = await  fetch(urlBack+"/playstate", requestOptions)
        return  await response.json()
    },
    revealHabitant:async(position,type)=>{
        // console.log(position[0])
        socket.emit(
            'revealHabitant',
            {
                y:parseInt(position[0]),
                x:parseInt(position[1]),
                type:parseInt(type)
            }
        )
        console.log("socket emit")
    },
    saveBudgetsInit:async(cycles,session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                cycles:cycles,
                session:session,
                equipe:equipe
            })
        };

        const response = await  fetch(urlBack+"/saveBudgetsInit", requestOptions)


        return await response.json()
    },
    saveBudgetsFin:async(budgetsFin, session,equipe)=>{
        const playState = await this.getPlayState(session,equipe)
        console.log(JSON.parse(playState.budgetsFin.valeur))
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                budgetsFin:budgetsFin,
                session:session,
                equipe:equipe
            })
        };


        const response = await  fetch(urlBack+"/saveBudgetFin", requestOptions)


        return await response.json()
    },
    getBudgetInit:async(session,equipe,numPompier,currentCycle,currentSequence)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                session:session,
                equipe:equipe,
                numPompier:numPompier,
                cycle:currentCycle,
                sequence:currentSequence
            })
        };

        const response = await  fetch(urlBack+"/budgetInit", requestOptions)


        return await response.json()
    },
    getBudgetsFin:async(session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                session:session,
                equipe:equipe
            })
        };

        const response = await  fetch(urlBack+"/budgetsFin", requestOptions)


        return await response.json()
    },

};
