import io from 'socket.io-client'

const urlBack = "http://localhost:4000";
const socket = io.connect(urlBack)

export default {
    startStopPartie:async(start)=>{
        console.log("start/stop partie")

        socket.emit(
            'startStopPartie',
            {
                start:start,
            }
        )

    },
    nouveauTour:async(currentPomp,carte,mursH,mursV,currentSequence,currentCycle,currentTour,resultatsDecedes,resultatsSauves,session,equipe,chrono)=>{
        console.log("nouveau tour")
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                carte:carte,
                mursH:mursH,
                mursV:mursV,
                currentPomp:currentPomp,
                currentSequence:currentSequence,
                currentCycle:currentCycle,
                currentTour:currentTour,
                resultatsDecedes:resultatsDecedes,
                resultatsSauves:resultatsSauves,
                session:session,
                equipe:equipe,
                currrentChrono:currrentChrono
            })
        };
        socket.emit(
            'changementTour',
            {
                currentSequence:parseInt(currentSequence),
                currentPomp :parseInt(currentPomp),
                currentTour :parseInt(currentTour),
                currentCycle :parseInt(currentCycle),
            }
        )

        const response = await  fetch(urlBack+"/nouveauTour", requestOptions)

        console.log("changementTour")
        return await response.json()

    },
    getSocket:()=>{
        return socket
    },
    getConfiguration:async(session)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({session:session})
        };
        const response = await  fetch(urlBack+"/configuration", requestOptions)
        return  await response.json()
    },
    getPlayState:async(session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                session:session,
                equipe:equipe
            })
        };
        const response = await  fetch(urlBack+"/playstate", requestOptions)
        return  await response.json()
    },
    revealHabitant:async(position,type)=>{
        // console.log(position[0])
        socket.emit(
            'revealHabitant',
            {
                y:parseInt(position[0]),
                x:parseInt(position[1]),
                type:parseInt(type)
            }
        )
        console.log("socket emit")
    },
    saveBudgetsPompInit:async(cycles,session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                cycles:cycles,
                session:session,
                equipe:equipe
            })
        };

        const response = await  fetch(urlBack+"/saveBudgetsPompInit", requestOptions)


        return await response.json()
    },
    saveBudgetsPompFin:async(budgetsFin, session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                budgetsFin:budgetsFin,
                session:session,
                equipe:equipe
            })
        };
        // console.log("budgets fin")

        const response = await  fetch(urlBack+"/saveBudgetsPompFin", requestOptions)
        console.log("save budgets fin")
        return  response
    },
    getBudgetsPompInit:async(session,equipe,numPompier,currentCycle,currentSequence)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                session:session,
                equipe:equipe,
                numPompier:numPompier,
                cycle:currentCycle,
                sequence:currentSequence
            })
        };

        const response = await  fetch(urlBack+"/getBudgetsPompInit", requestOptions)


        return await response.json()
    },
    getBudgetsPompFin:async(session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                session:session,
                equipe:equipe
            })
        };

        const response = await  fetch(urlBack+"/getBudgetsPompFin", requestOptions)


        return await response.json()
    },
    updateCurrentChrono:async(chrono,session,equipe)=>{
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                chrono:chrono,
                session:session,
                equipe:equipe
            })
        };

        const response = await  fetch(urlBack+"/updateChrono", requestOptions)

        return await response.json()
    }

};
