import React, { useState, useEffect } from 'react'
import CaseDonnee from './panels/CaseDonnee'
import '../styles/BandeauTop.css';
import BookmarkRoundedIcon from '@material-ui/icons/BookmarkRounded';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';

import ContentsAPI from "../API/ContentsAPI"

const BandeauTop = ({
    numeroPompier,
    cycle,
    tour,
    interventions,
    resultats

}) => {

    const [textes, setTextes] = useState(null)

    useEffect(() => {
        // console.log("use effect")
        const getTextes = async () => {
            const textes = ContentsAPI.getTexts("jeuBandeauTop").then(
                (res) => { setTextes(res) }
            )
        }
        if (textes == null) {
            getTextes()
        }
    }, [textes])

   

    const getText = (name) => {
        if (textes == null) {
            return name
        } else {
            return textes["label" + name.charAt(0).toUpperCase() + name.slice(1)].contenu
        }
    }

    const renderStocks = () => {
        return (
            <div
                className="case-panel donnees-container"
                style={{
                    flex: 3
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("stockEquipements")}
                </label>
                <div className="sous-donnees-container">
                    <CaseDonnee
                        key={0}
                        name={getText("nbExtFeu")}
                        value={interventions.nbExtFeu}
                        primary={false}
                        flexFactor={1}
                    />
                    <CaseDonnee
                        key={1}
                        name={getText("nbExtFum")}
                        value={interventions.nbExtFum}
                        primary={false}
                        flexFactor={1}
                    />
                </div>
                <div className="sous-donnees-container">
                    <CaseDonnee
                        key={2}
                        name={getText("nbExtFeuFum")}
                        value={interventions.nbExtFeuFum}
                        primary={false}
                        flexFactor={2}
                    />
                    <CaseDonnee
                        key={3}
                        name={getText("nbMarqueurs")}
                        value={interventions.nbMarqueurs}
                        primary={false}
                        flexFactor={2}
                    />
                </div>
            </div>
        )
    }

    const renderResults = () => {
        return (
            <div
                className="case-panel donnees-container"
                style={{
                    flex: 2
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("resultats") + " " + numeroPompier}
                </label>
                <CaseDonnee
                    key={4}
                    name={getText("nbDecedes")}
                    value={resultats.nbDecedes}
                    primary={false}
                    flexFactor={0}
                />
                <CaseDonnee
                    key={5}
                    name={getText("nbSauves")}
                    value={resultats.nbSauves}
                    primary={false}
                    flexFactor={0}
                />
            </div>
        )
    }

    const renderNumPompier = () => {
        return (
            <div
                className="case-panel"
                style={{
                    flex:1
                  }}
            >
                <label
                    className="label-title"
                >
                    {getText("numPompier")}
                </label>
                <div className="donnees-container">
                    <label
                        className="label-title label-num-pompier"
                    >
                        {numeroPompier}
                    </label>
                    <BookmarkRoundedIcon
                        className="icone-num-pompier"
                        fontSize="large"
                        stroke={"white"}
                        strokeWidth={1}
                    />
                </div>
            </div>
        )
    }

    const renderCircleTours = () => {
        var type = 1, //circle type - 1 whole, 0.5 half, 0.25 quarter
            radius = 20, //distance from center
            start = -90, //shift start from 0
            // $elements = $('li:not(:first-child)'),
            numberOfElements = 6,
            slice = 360 * type / numberOfElements;

        const renderCircleItems = () => {
            var items = []
            for (let i = 0; i < numberOfElements; i++) {
                const rotate = slice * i + start
                const rotateReverse = rotate * -1
                items.push(
                    <li 
                    key={i}
                    style={{ 
                        'transform': 'rotate(' + rotate + 'deg) translate(' + radius + 'px) rotate(' + rotateReverse + 'deg) translate(' + -radius/2 + 'px)',
                        "backgroundColor":i+1==tour ? "#BF0000" : null
                    }}
                    
                    >
                        {i + 1}
                    </li>

                )
            }
            return items
        }

        return (
            <div
                className="case-panel"
                style={{
                  flex:1
                }}
            >
                <label
                    className="label-title"
                >
                    {getText("tourParTour")}
                </label>
                <ul >
                    {renderCircleItems()}
                </ul>
            </div>
        )
    }

    const renderFeu=()=>{
        return (
            <div
                className="case-panel"
                style={{
                    flex:1
                  }}
            >
                <label
                    className="label-title"
                >
                    {getText("play")}
                </label>

                <div className="donnees-container">
                   <div className='feu'>
                       <div style={{backgroundColor: numeroPompier!=tour ? " #ff0000" : "#9F9F9F"}}></div>
                       <div style={{backgroundColor: numeroPompier==tour ? "#4dff4d" : "#9F9F9F"}}></div>
                   </div>
                </div>




            </div>
        )
    }
    

    return (
        <div className="bandeau-top">
            <div className="cases-container">
                <CaseDonnee
                    name={getText("cycle")}
                    value={cycle}
                    primary={true}
                    flexFactor={1}

                />
                <CaseDonnee
                    name={getText("tour")}
                    value={tour}
                    primary={true}
                    flexFactor={1}
                />
                <CaseDonnee
                    name={getText("nbInterventions")}
                    value={interventions.nbInterventions}
                    primary={true}
                    flexFactor={1}
                />
                {renderStocks()}
                {renderResults()}
                {renderCircleTours()}
                {renderNumPompier()}
                {renderFeu()}

            </div>

        </div>


    );
}

export default BandeauTop;
