import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React,{useState,useEffect} from 'react'

const MurPiece = ({
    num,
    height,
    width,
    type
}) => {
    const epaisseur=type>0 ? 4 : 1
    
    // console.log(type)
    
    const colors =[
        "", // simple
        // "red",
        "black", // large
        "purple",//porte closed
        "purple",//porte open
    ]

   

    return ( 
        <div        
            style={{
                
                position:"absolute",
                width:num%2==0 ? width+2 : epaisseur,
                height:num%2==1 ? height+1 :  epaisseur , 
                right:num==1 ? -1 :  null,
                left:num%2==0 || num==3 ? -1 : null,
                top:num==0 || num%2==1 ? -1 : null,
                bottom:num==2 ? 0 :  null,
                backgroundColor:(type>0) ? colors[type] : 'transparent',
                zIndex: type==1 ? 2 : 1,
                transform: type==3 ? 'rotate(90deg) translate('+0+'px,'+(-width)+'px)' : ''
            }}
        >
        </div>

    );
}
 
export default MurPiece;
