import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
      maxWidth: 360,
      backgroundColor: theme.palette.background.paper,
      position: 'relative',
      overflow: 'auto',
      maxHeight: 300,
    },
    listSection: {
      backgroundColor: 'inherit',
    },
    ul: {
      backgroundColor: 'inherit',
      padding: 0,
    },
  }));
  
const ListCompanies=({
    companies,
    input,
    handlerClic
})=>{

    const classes = useStyles();


    return (
        <List className={classes.root} >
            {companies.map((comp,index)=>{

              const indexStr = comp.name.toUpperCase().indexOf(input.toUpperCase())

              return(
                <ListItem button key={index} onClick={(e)=>{
                  e.preventDefault()
                  handlerClic(comp)
                }}>

                  <ListItemText > {comp.name.substring(0, indexStr)}
                    <strong>
                        {comp.name.substring(indexStr, indexStr+input.length)}
                    </strong>
                    {comp.name.substring(indexStr+input.length, comp.name.length)}
                  </ListItemText>
                </ListItem>
              )
               
            }
            
            
            )}
           
            
        </List>

    )
}

export default ListCompanies
