import React from 'react';
import { makeStyles } from '@material-ui/core/styles';

import './Grille.css';

import Piece from "./pieces/Piece"



const Grille = ({
    nbColonnes,
    nbLignes,
    carte,
    position,
    mursH,
    mursV
}) => {

    const getDistance=(min,max)=>{
        return Math.sqrt((min[0]-max[0])*(min[0]-max[0]) + (min[1]-max[1])*(min[1]-max[1]))
    }

    const associate=(minimums,maximums)=>{
        var anchors=[]
        var maxUsed=[]

        maximums.sort((a,b)=>b[1]-a[1]).sort((a,b)=>a[0]-b[0])

        minimums.forEach(min => {
            var Max =[nbLignes,nbColonnes]
            maximums.forEach(max => {
                if((max[0]>=min[0] && max[1]>=min[1]) && (max[0]<=Max[0] &&  max[1]<=Max[1]) && (min[0]<max[0] || min[1]<max[1]) && !maxUsed.includes(max)){
                    Max = max
                }
            });
            maxUsed.push(Max)
            anchors.push([min,Max])
        })
       return(anchors)
    }

    const getAnchors=()=>{   
        var minimums=[]
        var maximums=[]
        for(let i=0;i<mursV.length;i++){
            for(let j=0;j<mursH[i].length;j++){
                if(mursV[i][j]>0 && mursH[i][j]>0){
                    minimums.push([i,j])
                }   
                if(mursV[i][j+1]>0 && mursH[i+1][j]>0){
                    maximums.push([i,j])
                }
            }
        }
        return associate(minimums,maximums)
    }

    const getAnchor=(anchors,pos)=>{
        
        var Min=[-1,-1]
        var Index=-1
        anchors.forEach((a,index) => {
            const min = a[0]
            const max = a[1]

            if(pos[0]>=min[0] && pos[1]>=min[1] && pos[0]<=max[0] && pos[1]<=max[1]){
                Min= min
                Index=index
            }
        });
        
        return [Min,Index]
    }

    const maxLignesPiece = 8
    const maxColonnesPiece=16

    
    

    const nbLignes1=8
    const nbColonnes1=16
    
    const renderPieces=()=>{
        var components =[]
        
        const anchors = getAnchors()

        const [anchor,idAnchor]=getAnchor(anchors,position) 

        let nbL
        let nbC

        if(anchor[0]== -1 && anchor[1]==-1){
            nbL = maxLignesPiece
            nbC =maxColonnesPiece
        }else{
            const area=anchors[idAnchor]
            nbL = anchor[1][0]-anchor[0][0]+1
            nbC = anchor[1][1]-anchor[0][1]+1
            
            console.log(nbL)
            console.log(nbC)
        }
        



        components.push(
            <Piece
                mursH={mursH}
                mursV={mursV}
                carte={carte}
                position={position}
                nbLignes={nbL}
                nbColonnes={nbC}
                maxColonnesPiece={maxColonnesPiece}
                maxLignesPiece={maxLignesPiece}
                anchor={[0,0]}
            />
        )
        return components
       
    }
    

    return ( 
        <div 
            className="piece-container"
        >
            {renderPieces()}
        </div>
    )
}
 
export default Grille;
