import { withWidth } from '@material-ui/core';
import React, { useState, useEffect } from 'react'

const MurPiece = ({
    num,
    height,
    width,
    type,
    coordX,
    coordY,
    nbColonnesPiece,
    nbLignesPiece,
    nbColonnesGrille,
    nbLignesGrille,
    anchor,
    propositionMursDetruire,
    handleClicMur,
    mursH,
    mursV
