import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import CasePiece from "./CasePiece"



const Piece = ({
    carte,
    idPiece,
    position,
    nbLignesGrille,
    nbColonnesGrille,
    nbLignesPiece,
    nbColonnesPiece,
    mursV,
    mursH,
    anchor

}) => {
    console.log("Piece nb grille : "+nbLignesGrille+", colonnes : "+nbColonnesGrille+" | piece lignes : "+nbLignesPiece+",  colonnes "+nbColonnesPiece+" => anchor i : "+anchor[0]+", j : "+anchor[1])
    // console.log()
    const renderRow=(i)=>{
        var row=[]
        for(let j=0;j<nbColonnesGrille+2;j++){
            const anchori = i+anchor[0]-1
            const anchorj = j+anchor[1]-1
            
            if (anchori<nbLignesPiece+anchor[0] &&  anchori>=anchor[0] && anchorj<nbColonnesPiece+anchor[1] &&  anchorj>=anchor[1] ){
                console.log("créé : ",anchori , anchorj)
                row.push( 
                    <CasePiece 
                        key={i+","+j}
                        coordX={anchorj}
                        coordY={anchori}
                        anchor={anchor}
                        nbColonnesGrille={nbColonnesGrille}
                        nbLignesGrille={nbLignesGrille}
                        nbColonnesPiece={nbColonnesPiece}
                        nbLignesPiece={nbLignesPiece}
                        murs={[mursH[anchori][anchorj],mursV[anchori][anchorj+1],mursH[anchori+1][anchorj],mursV[anchori][anchorj]]}
                        type={ j>nbColonnesGrille || i>nbLignesGrille ? 0 : carte[anchori][anchorj] }
                        
                    />
                )
            }else{
               
                let newI = anchori
                let newJ = anchorj
                var murs =[0,0,0,0]
                
                if(anchori<anchor[0]){ // haut
                    newI = anchori+1
                    newJ = anchorj+0
                    // console.log("new ",newI,newJ,mursV[newI][newJ+1])
                    try{
                        murs[1]=mursV[newI-1][newJ+1]>0 ? mursV[newI-1][newJ+1] : 0
                    }catch(error){
                    }
                    try{
                        murs[3]=mursV[newI-1][newJ]>0 ? mursV[newI-1][newJ] : 0
                    }catch(error){

                    }
                    try{
                        murs[2]=mursH[newI][newJ]>0 ? mursH[newI][newJ] : 0
                    }catch(error){
                    }
                    
                    
                }
                if(anchori>=nbLignesGrille+anchor[0]){ // bas
                    newI = anchori
                    newJ = anchorj
                    // console.log("new ",newI,newJ,mursH[newI][newJ])
                    try{
                        murs[0]=mursH[newI][newJ]>0 ? mursH[newI][newJ] : 0
                    }catch(error){
                    }
                   
                    try{
                        murs[1]=mursV[newI][newJ+1]>0 ? mursV[newI][newJ+1] : 0
                    }catch(error){
                    }
                    try{
                        murs[3]=mursV[newI][newJ]>0 ? mursV[newI][newJ] : 0
                    }catch(error){

                    }
                   
                   
                }
                if(anchorj<anchor[1]){ // gauche
                    newI = anchori
                    newJ = anchorj
                    try{
                        murs[0]=mursH[newI][newJ]>0 ? mursH[newI][newJ] : 0
                    }catch(error){
                    }
                    try{
                        murs[2]=mursH[newI+1][newJ]>0 ? mursH[newI+1][newJ] : 0
                    }catch(error){
                    }

                    try{
                        murs[1]=mursV[newI][newJ+1]>0 ? mursV[newI][newJ+1] : 0
                    }catch(error){
                    }
                    
                }
                if(anchorj>=nbColonnesGrille+anchor[1]){ // droite
                    newI = anchori
                    newJ = anchorj
                    try{
                        murs[0]=mursH[newI][newJ]>0 ? mursH[newI][newJ] : 0
                    }catch(error){
                    }
                    try{
                        murs[2]=mursH[newI+1][newJ]>0 ? mursH[newI+1][newJ] : 0
                    }catch(error){
                    }

                    try{
                        murs[3]=mursV[newI][newJ]>0 ? mursV[newI][newJ] : 0
                    }catch(error){
                    }
                }



                // console.log("annulé : ",anchori , anchorj)
                // newI=0
                // newJ=0
                row.push( 
                    <CasePiece 
                        key={i+","+j}
                        coordX={anchorj}
                        coordY={anchori}
                        anchor={anchor}
                        nbColonnesGrille={nbColonnesGrille}
                        nbLignesGrille={nbLignesGrille}
                        nbColonnesPiece={nbColonnesPiece}
                        nbLignesPiece={nbLignesPiece}
                        murs={murs}

                        type={5}
                        
                    /> 
                )
                

            }
            
        }
        return row
    }


    const renderGrille=()=>{
        var grille=[]
        for (let i=0;i<nbLignesGrille+2;i++){
            grille.push(
                <div key={"row"+i} className="row-piece" >
                    {renderRow(i)}
                </div>
            )  
            
        }
        return grille
    }



    return (  

    <div
        className="piece"  
        style={{
            position:"absolute",
            width:nbColonnesPiece/nbColonnesGrille*80+"%",
            height:nbLignesPiece/nbLignesGrille*100+"%",
            
        }} 
       
    >
       {renderGrille()}
    </div>
        
        
    );
}
 
export default Piece;
