import { withWidth } from '@material-ui/core';
import React,{useState,useEffect} from 'react'

const MurGrille = ({
    type,
    vertical
}) => {
    
    const colorMur="black"

    return ( 
       <div 
            className="mur" 
            style={{
                top:0,
                backgroundColor:"yellow",
                width:vertical ? 10 : "5%",
                height:vertical ?  "5%" : 10,
            }}>
            {type}
       </div>

    );
}
 
export default MurGrille;
