import { withWidth } from '@material-ui/core';
import zIndex from '@material-ui/core/styles/zIndex';
import React,{useState,useEffect} from 'react'

const MurPiece = ({
    num,
    height,
    width,
    type
}) => {
    const [epaisseur,setEpaisseur]=useState(type>0 ? 4 : 1)
    
    
    const colors =[
        "", // simple
        // "red",
        "black", // large
        "purple"//porte
    ]

    // const height = document.getElementById('test').style.height;
    // console.log(height)

   

    return ( 
        <div        
            
            style={{
                
                position:"absolute",
                width:num%2 ? epaisseur : (epaisseur>1 ? width+2 :width-(epaisseur+1)*2),
                height:num%2 ? (epaisseur>1 ? height+2 :height-(epaisseur+1)*2) :  epaisseur , 
                right:num<2 ? (num==0 ? (epaisseur>1 ? -epaisseur : -epaisseur) : -epaisseur) :  null,
                left:num>1 ? (num==2 ? (epaisseur>1 ? -epaisseur : -epaisseur)  : -epaisseur) :  null,
                top:num==1 || num==3 ?  -epaisseur :    0,
                bottom:num==2 ? -epaisseur :  null,
                backgroundColor:type>0 ? colors[type] : 'transparent',
                zIndex: epaisseur>1 ? (type==1 ? 2 : 1) : 0
            }}
        >
        </div>

    );
}
 
export default MurPiece;
