import React,{useRef,useState,useEffect,useLayoutEffect} from 'react'
import MurPiece from "./MurPiece"

const CasePiece = ({
    murs,
    type,
    coordX,
    coordY,
    nbColonnesPiece,
    nbLignesPiece,
    nbColonnesGrille,
    nbLignesGrille,
    anchor
}) => {
    const [height,setHeight]=useState(0)
    const [width,setWidth]=useState(0)
    
    const colors=[
        "#F0F0F0", // 0 vide
        "red", // 1 pompier
        "green", // 2 habitant
        "", // 3 fumée
        "yellow", // 4 flamme
        
        "#D1D1D1" // 5 caché
    ]


    const element =useRef()
    
    useLayoutEffect(() => {
        function updateSize() {
            setHeight(element.current.clientHeight)
            setWidth(element.current.clientWidth)
        }
        window.addEventListener('resize', updateSize);
        updateSize();
        return () => window.removeEventListener('resize', updateSize);
    }, []);

    const renderMurs=()=>{
        var mursComponents=[]
        for(let i=0;i<4;i++){
            
            mursComponents.push(
                <MurPiece 
                    key={i}
                    num={i} 
                    height={height}
                    width={width}
                    type={murs[i]}
                    coordX={coordX}
                    coordY={coordY}
                    anchor={anchor}
                    maxColonnesPiece={ maxColonnesPiece}
                    maxLignesPiece={maxLignesPiece}
                    nbColonnesGrille={nbColonnesGrille}
                    nbLignesGrille={nbLignesGrille}
                    
                />
            )
        }
        return (
            <>
                {mursComponents}
            </>
        )
    }

    const renderCase=()=>{

        if (type==1) return (<img style={{maxHeight:height*0.9,maxWidth:width}} src={"assets/Pompier.png"} alt={"image"}/>)
        if (type==2) return (<img style={{maxHeight:height,maxWidth:width*0.8}} src={"assets/Pompier.png"} alt={"image"}/>)
        if (type==3) return (<img style={{maxHeight:height,maxWidth:width*0.8}} src={"assets/Fumée"+Math.floor(Math.random()*5)+".png"} alt={"image"}/>)
        if (type==4) return (<img style={{maxHeight:height,maxWidth:width*0.8}} src={"assets/Flamme.png"} alt={"image"}/>)
        return null
    }
  
    

    return ( 

        <div 
            ref={ element }
            className='case-piece'  
            style={{
                backgroundColor:colors[type]
            }}
        >
           {renderMurs()}
           {renderCase()}

        </div>

    );
}
 
export default CasePiece;
