import './App.css';
import React,{useState,useEffect} from 'react'
import io from 'socket.io-client'

import BandeauTop from "./components/BandeauTop"
import BandeauBottom from "./components/BandeauBottom"
import Grille from './components/Grille'

const socket = io.connect('http://localhost:4000')

function App() {

  const [numPompier,setNumPompier]=useState(1)
  const [currentTour,setCurrentTour]=useState(1)
  const [currentCycle,setCurrentCycle]=useState(1)

  const nbInterventions=4
  const nbExtFeu=4
  const nbExtFeuFum=7
  const nbExtFum=1
  const nbMarqueurs=5
  const nbDecedes=1
  const nbSauves=5

  const nbInterFeu=2
  const nbInterFeuFum=1
  const nbInterFumee=1
  const nbInterMarqueurs=1
  const nbInterOuvrirPorte=1
  const nbInterFermerPorte=1
  const nbInterHabitant=2

  const nbColonnes=12
  const nbLignes=8





  const onTextChange=(e)=>{
    setNumPompier(e.target.value)
    console.log(typeof(e.target.value))
  }


  const onTourChange=(e)=>{
    e.preventDefault()
    socket.emit('tour',parseInt(numPompier))
  }


  useEffect(() => {
    socket.on('tour',num=>{
      setCurrentTour(parseInt(num)+1)
    })
  });


  return (
    <>

    <div className="card">
      <form  onSubmit={e=>onTourChange(e)}>
        <h1>Messenger</h1>
        <div className="name-field">
          <label htmlFor="numPompier">NumÃ©ro pompier</label>
          <input
            type="number"
            name="numPompier"
            onChange={e=>onTextChange(e)}
            value={numPompier}
            label="NumÃ©ro pompier"
          />
        </div>

        {/* <input type='hidden' value={numPompier} name="numPompier"/> */}

      {currentTour==numPompier ? 
        <button>Fin du tour</button>
      :
      <button disabled>Fin du tour</button>

      }

      </form>

    </div>

    <Grille
      nbColonnes={nbColonnes}
      nbLignes={nbLignes}
    />
    <BandeauTop
      numeroPompier={numPompier}
      cycle={currentCycle}
      tour={currentTour}
      nbInterventions={nbInterventions}
      nbExtFeu={nbExtFeu}
      nbExtFum={nbExtFum}
      nbExtFeuFum={nbExtFeuFum}
      nbExtFeuFum={nbExtFeuFum}
      nbMarqueurs={nbMarqueurs}
      nbDecedes={nbDecedes}
      nbSauves={nbSauves}
    />
    <BandeauBottom
       nbInterFeu={nbInterFeu}
       nbInterFumee={nbInterFumee}
       nbInterFeuFum={nbInterFumee}
       nbInterMarqueurs={nbInterMarqueurs}
       nbInterOuvrirPorte={nbInterOuvrirPorte}
       nbInterFermerPorte={nbInterFermerPorte}
       nbInterHabitant={nbInterHabitant}
    />
    </>
  );
}

export default App;
