import React, { useState, useEffect,useRef } from 'react'
import ToggleButton from '@material-ui/lab/ToggleButton';
import "../../../styles/TactBandeauBottom.css"
import Bulle from './Bulle';

const BoutonAction = ({
    action,
    nbIntervention,
    nbStock,
    sourceImage,
    handleClicAction,
    selected,
    num,
    disabled
}) => {
    const [bulleOpen,setBulleOpen]=useState(true)

    const handleOpen = () => {
        setBulleOpen(true)
        console.log("open")
    };

    const handleClose = () => {
        setBulleOpen(false)
        console.log("close")
    };

    let timerOver
    const handleOver=()=>{
        timerOver=setTimeout(() => {
            handleOpen()
        }, 1500);
    }





    return (
        <>
            <ToggleButton
                disabled={disabled || nbIntervention <= 0 || nbStock <= 0}
                selected={selected && ![4, 5].includes(num)}
                className="bouton-action"
                variant='contained'
                value={num}
                onClick={() => {
                    handleClicAction(num)
                }}
                onMouseEnter={() => {
                    handleOver()
                }}
                onMouseLeave={() => {
                    clearTimeout(timerOver)
                }}

            >
                {/* <div> */}



                <label className="title-action">{action + "\n"}</label>
                <img src={sourceImage} alt={"image"} />
                {nbIntervention > 1 ?
                    <label className="count-action">{nbIntervention + " interventions"}</label>

                    :

                    <label className="count-action">{nbIntervention + " intervention"}</label>
                }
                {/* </div> */}

            </ToggleButton>

            <Bulle
                num={num}
                open={bulleOpen}
                handleClose={handleClose}
                texte={"info n°"+num}
            />
        </>
    );
}

export default BoutonAction;
