import React,{useState,useEffect} from 'react'
import CaseDonnee from './CaseDonnee'
import './BandeauTop.css';

const BandeauTop = ({
    numeroPompier,
    cycle,
    tour,
    nbInterventions,
    nbExtFeu,
    nbExtFum,
    nbExtFeuFum,
    nbMarqueurs,
    nbDecedes,
    nbSauves

}) => {
    
    const renderFeu = ()=>{
    
        return (

            (tour%6)==numeroPompier ? 
            <button className="bouton-vert"></button>
            :
            <button className="bouton-rouge"></button>
        
        )
    }

    return (  
        <div className="bandeau-top">
            <CaseDonnee
                name={"cycle"}
                value={cycle}
            />
            <CaseDonnee
                name={"tour"}
                value={tour}
            />
            <CaseDonnee
                name={"nbInterventions"}
                value={nbInterventions}
            />
            <CaseDonnee
                name={"nbExtFeu"}
                value={nbExtFeu}
            />
            <CaseDonnee
                name={"nbExtFeuFum"}
                value={nbExtFeuFum}
            />
            <CaseDonnee
                name={"nbExtFum"}
                value={nbExtFum}
            />
            <CaseDonnee
                name={"nbMarqueurs"}
                value={nbMarqueurs}
            />
            <CaseDonnee
                name={"nbDecedes"}
                value={nbDecedes}
            />
            <CaseDonnee
                name={"nbSauves"}
                value={nbSauves}
            />
            <CaseDonnee
                name={"numPompier"}
                value={numeroPompier}
            />
            {renderFeu()}
        </div>
       

    );
}
 
export default BandeauTop;
