import axios from "axios";
const headers = {
  "Content-Type": "application/json"
};
const urlBack = "http://localhost:4000";

export default {
    companies: (str)=>{
        return axios.post(
            urlBack+"/companies",
            {
                str
            },
            { 
               headers: headers
            }
        )
    }
};
