import React,{useState,useEffect} from 'react'
import Button from '@material-ui/core/Button'
import '../../styles/BandeauTop.css';
// import ImageExtFeu from "../../assets/ExtincteurFeu.png"

const BoutonAction = ({
    action,
    nb,
    sourceImage,
    handleClicAction
}) => {


    return (  
        <Button selected={true} className="bouton-action" variant='contained' disableElevation onClick={()=>handleClicAction(action)}>
            {/* <div> */}

            

                <label className="title-action">{action+"\n"}</label>
                <img src={sourceImage} alt={"image"}/>
                {nb>1 ? 
                <label className="count-action">{nb+" interventions"}</label>

                :

                <label className="count-action">{nb+" intervention"}</label>
                }
            {/* </div> */}
            
        </Button>
            
       

    );
}
 
export default BoutonAction;
